import React, { useState } from "react";
import InputField from "./InputField";
import SelectField from "./SelectField";
import { educationOptions } from "../constants/form";
import Section from "./Section";

const defaultForm = {
  email: "",
  fullName: "",
  age: "",
  education: "",
  institute: "",
  program: "",
  experience: "",
  canadaInstitute: "",
  canadaProgram: "",
  coutry: "",
  goals: "",
  scoresListening: "",
  scoresReading: "",
  scoresSpeaking: "",
  scoresWriting: "",
  isPaidTutionFees: null,
  paidTutionAmount: "",
  isGic: null,
  paidGicAmout: "",
};

export default function Form() {
  const [form, setForm] = useState(defaultForm);

  const handleChange = (e, name) => {
    const value = e.target.value;
    setForm((prevState) => ({ ...prevState, [name]: value }));
  };

  console.log(form, "form");

  return (
    <div className="container">
      <form>
        <Section>
          <InputField
            label="Email"
            name="email"
            type="email"
            value={form.email}
            onChange={handleChange}
          />
        </Section>

        <InputField
          label="Full Name"
          name="fullName"
          type="text"
          value={form.fullName}
          onChange={handleChange}
        />
        <InputField
          label="Age"
          name="age"
          type="number"
          value={form.age}
          onChange={handleChange}
        />
        <SelectField
          label="Education"
          name="education"
          options={educationOptions}
          value={form.education}
          onChange={handleChange}
        />
        <InputField
          label="Institute"
          name="institute"
          type="text"
          value={form.institute}
          onChange={handleChange}
        />
        <InputField
          label="Experience"
          name="experience"
          type="number"
          value={form.experience}
          onChange={handleChange}
        />
      </form>
    </div>
  );
}
