export const SchemaTypes = {
  string: (required = false, message) => ({
    type: "string",
    required,
    message,
  }),
  number: (required = false, message) => ({
    type: "number",
    required,
    message,
  }),
  boolean: (required = false, message) => ({
    type: "boolean",
    required,
    message,
  }),
};

const validateData = (schema, data) => {
  const errors = [];
  Object.keys(data).forEach((key) => {
    const schemaTypes = schema[key];
    const type = schemaTypes.type;


    let value = data[key];

    try {
      value = JSON.parse(value);
    } catch (error) {
      value = value;
    }

    if (typeof value !== type) {
      errors.push({
        [key]: `Expected ${type} for ${key} but received ${typeof value}`,
      });
    }
  });
};
