import React, { useState } from "react";
import InputField from "./InputField";
import SelectField from "./SelectField";
import { educationOptions } from "../constants/form";

const defaultForm = {
  email: "",
  fullName: "",
  age: "",
  education: "",
  institute: "",
  program: "",
  experience: "",
  canadaInstitute: "",
  canadaProgram: "",
  coutry: "",
  goals: "",
  scoresListening: "",
  scoresReading: "",
  scoresSpeaking: "",
  scoresWriting: "",
  isPaidTutionFees: null,
  paidTutionAmount: "",
  isGic: null,
  paidGicAmout: "",
};

export default function Form() {
  const [form, setForm] = useState(defaultForm);

  const handleChange = (e, name) => {
    const value = e.target.value;
    setForm({[name]: value })
  }

  return (
    <div>
      <form>
        <InputField name="email" type="email" value={form.email} onChange={handle} />
        <InputField name="fullName" type="text" value={form.fullName} />
        <InputField name="age" type="number" value={form.age} />
        <SelectField
          name="education"
          options={educationOptions}
          value={form.education}
        />
        <InputField name="institute" type="text" value={form.institute} />
        <InputField name="experience" type="text" value={form.experience} />
      </form>
    </div>
  );
}
