export const SchemaTypes = {
  string: (required = false, message) => ({
    type: "string",
    required,
    message,
  }),
  number: (required = false, message) => ({
    type: "number",
    required,
    message,
  }),
  boolean: (required = false, message) => ({
    type: "boolean",
    required,
    message,
  }),
};

export const formatValue = (value) => {
  try {
    value = JSON.parse(value);
  } catch (error) {
    value = value;
  }
};

export const validateData = (schema, data) => {
  const errors = {};
  Object.keys(data).forEach((key) => {
    const schemaTypes = schema[key];
    const value = formatValue(data[key]);

    if (typeof value !== schemaTypes.type) {
      errors[key]({
        [key]: `Expected ${
          schemaTypes.type
        } for ${key} but received ${typeof value}`,
      });
    } else if (
      schemaTypes.required &&
      (value === null || value === undefined || value === "")
    ) {
      errors.push({
        [key]: schemaTypes.message || `${key} is Required!`,
      });
    }
  });
  return errors;
};
