import React from "react";

export default function InputField({ label, value, onChange, type, name }) {
  const handleChange = (e) => {
    onChange(e, name);
  };

  const RadioInput = () => {
    return (
      <>
        <input value={value} type={type} onChange={handleChange} />
        <label>Yes</label>
        <input value={value} type={type} onChange={handleChange} />
        <label>No</label>Æ’
      </>
    );
  };

  const Input = () => {
    return (
      <>
        <label aria-required>{label}</label>
        <input value={value} type={type} onChange={handleChange} />
      </>
    );
  };

  return (
    <div className="input-field">
      {type === "radio" ? <RadioInput /> : <Input />}
    </div>
  );
}
