export const SchemaTypes = {
  string: (required = false, message) => ({
    type: "string",
    required,
    message,
  }),
  number: (required = false, message) => ({
    type: "number",
    required,
    message,
  }),
  boolean: (required = false, message) => ({
    type: "boolean",
    required,
    message,
  }),
};

export const formatValue = (value, type) => {
  try {
    if (type !== "string") {
      value = JSON.parse(value);
    }
  } catch (error) {
    value = value;
